package com.syntax.class2;

public class IncrementOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=10;
		System.out.println(number);
		number=number+1;
		System.out.println(number);
		number++; //increments the value by 1
		System.out.println(number);
		
		
		

	}

}
