package com.syntax.class5;

public class SwitchCases {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int month=2;
		switch(month) {
		
		case 1:
			System.out.println("Jan");
			break;
		case 2:
			System.out.println("Feb");
			break;
		case 3:
			System.out.println("March");
			break;
		}

	}

}
